sqlMap.select:

/**!mycat:datanode=$db$**/ select fl.wgxlb,fl.wgxmc,fl.qsrq,fl.jsrq from wgjf_wgxmfl fl where 1 = 1 
and fl.wgxmc like '%$flmc$%'  
and $sysdate$ between fl.qsrq and fl.jsrq


/**!mycat:datanode=$db$**/ select count(1) from ( select fl.wgxlb,fl.wgxmc,fl.qsrq,fl.jsrq from wgjf_wgxmfl fl where 1 = 1 
and fl.wgxmc like '%$flmc$%'  
and $sysdate$ between fl.qsrq and fl.jsrq ) t
