sqlMap.select:

/**!mycat:datanode=$db$**/ select zb.khzbmc as khzbmc, jglb.lbmc as jglb,'$qsrq$' as qsrq from khfa_khzb_jg zb , (select '-1' as lbdh,'全行' as lbmc from csb_jglb union select lbdh ,lbmc from csb_jglb ) jglb where 1=1 
and zb.khzbdh in ($khzbdh$)  
and jglb.lbdh='$jglb$' order by zb.khzbdh
