jQuery(document).ready(function () {
   $('#cxfs').change(function(){
    var cxfs = $(this).val();
    var label = $('#khdxdh_mc').parent().prev();
    if(cxfs=='1'){//操作员
      label.text('操作员');
    }else if(cxfs=='2'){//行员
      label.text('行员');
    }
  }).trigger('change');


  var o_qsrq = createTimeObj($("#qsrq").val());
  $("#qsrq").val(o_qsrq.yc);

});

