pageSetting.javascript:
jQuery(document).ready(function () {
   jQuery('#ywlb').bind('change',setSjypdzd).trigger('change');
   jQuery('#zhlx').bind('change',setSjypdzd).trigger('change');
   init_ywlb().pipe(init_zhlx).pipe(function(){
      setSjypdzd();
    });

    PAS.di.execute('gryjmx_ckyjmxcsz',checkZzhBackCk);//判断有没有子账号
    PAS.di.execute('gryjmx_dkyjmxcsz',checkZzhBackDk);//判断有没有子账号
});

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
 return true;
}

function checkZzhBackCk(obj){

  if(obj.data.length>0){
     if(obj.data[0].CSZ=='0'){
        $('#ckcsz').val(0); //0表示没有子账号，所以需要隐藏
     }}
}

function checkZzhBackDk(obj){
  if(obj.data.length>0){
     if(obj.data[0].CSZ=='0'){
        $('#dkcsz').val(0); //0表示没有子账号，所以需要隐藏
     }}
}

function customOnLoadSuccess(data){
var ckcsz = $('#ckcsz').val();
var dkcsz = $('#dkcsz').val();
var ywlb = $('#ywlb').val();

if(ywlb == '01'){
   if(ckcsz == '0'){
    jQuery('#gryjmxList').datagrid('hideColumn','ZZH');
  }
}
if(ywlb == '02'){
   if(dkcsz == '0'){
    jQuery('#gryjmxList').datagrid('hideColumn','ZZH');
  }
}

}

function setSjypdzd(){
  var ywlb=jQuery('#ywlb').val();
  var zhlx=jQuery('#zhlx').val();
  jQuery('#sjypdzd').val(ywlb+"_"+zhlx);

}

function showDkZhye(obj){
  //var paramsJasonString = "{"+"'tjrq':'"+$("#tjrq").val().substr(0,4)+"','jxdxdh':'"+obj['JXDXDH']+"','ywlb':'1'}";
  //querySubQuery(paramsJasonString,"zhyebdmx",'80','40','tag'); 
  custom_window('/module/gryjmx/index.html?tjrq='+$("#tjrq").val()+'&jxdxdh='+obj['JXDXDH']+'&showimg=true&ywlb=2',940,500,'余额变动明细');
}

function showCkZhye(obj){
  //var paramsJasonString = "{"+"'tjrq':'"+$("#tjrq").val().substr(0,4)+"','jxdxdh':'"+obj['JXDXDH']+"','ywlb':'2'}";
  //querySubQuery(paramsJasonString,"zhyebdmx",'80','40','tag'); 
  custom_window('/module/gryjmx/index.html?tjrq='+$("#tjrq").val()+'&jxdxdh='+obj['JXDXDH']+'&showimg=true&ywlb=1',700,500,'余额变动明细');
}
