/**!mycat:datanode=$db$**/ select t.*,hy.hymc,hy.hydh from (select ywl.*, rank() over(order by ywl.ywlhj desc) as qxpm, rank() over(partition by ywl.jgmc order by ywl.ywlhj desc) as wdpm, rank() over(partition by ywl.lbmc order by ywl.ywlhj desc) as tljgpm from (select hy.khdxdh,'$qsrq$'||'-'||'$jsrq$' as tjsjd,jg.jgmc,lb.lbmc, round(sum(ywl.zhywlhj),2) as ywlhj, round(sum(ywl.xtzqywl),2) as xtzq,round(sum(ywl.xtzhywl),2) as xtzh, round(sum(ywl.sgzqywl),2) as sgzq,round(sum(ywl.sgzhywl),2) as sgzh, round(sum(ywl.bcywl),2) as bcywl, round(sum(ywl.zqxjsrl+ywl.zqxjfcl)/10000,2) as zqxjsf,round(sum(ywl.zhxjsrl+ywl.zhxjfcl),2) as zhxjsf from nbzz_hykh_ywl ywl inner join khdx_hy hy on ywl.khdxdh = hy.khdxdh 
and ywl.tjrq between $qsrq$ and $jsrq$ inner join khdx_jgcy cy on cy.khdxdh = hy.khdxdh and ywl.tjrq between cy.qsrq and cy.jsrq inner join khdx_jg jg on jg.khdxdh = cy.jgkhdxdh inner join khdx_hylb hylb on hylb.khdxdh = hy.khdxdh and ywl.tjrq between hylb.qsrq and hylb.jsrq inner join csb_hylb lb on lb.lbdh = hylb.lbdh group by hy.khdxdh,'$qsrq$'||'-'||'$jsrq$', jg.jgmc, lb.lbmc) ywl) t inner join table(FN_GETSJJG_JAVA_QXHY($sysdate$,$login_khdxdh$,0)) hy on hy.khdxdh = t.khdxdh 
where t.khdxdh in ($khdxdh$) order by t.qxpm


