/**!mycat:datanode=$db$**/ select substr(cast(gzhz.tjrq as char(8)),1,6) as tjrq, jg.jgdh, jg.jgmc, hy.hydh, hy.hymc, gzhz.dkyjjl, gzhz.dkzlkf, gzhz.bldkqsjl, gzhz.yfjxgz,jxgz.LXSRJX,jxgz.KHGLJX,jxgz.DKFFJX,jxgz.FYJDKKF,jxgz.FYJDKKFFH,jxgz.DKYJKZXM1, jxgz.DKYJKZXM2,jxgz.DKYJKZXM3,jxgz.DKKFKZXM1,jxgz.DKKFKZXM2 from dkjx_dkjxgzhz gzhz inner join khdx_hy hy on hy.khdxdh = gzhz.khdxdh inner join khdx_jgcy jgcy on jgcy.khdxdh = gzhz.khdxdh and gzhz.tjrq between jgcy.qsrq and jgcy.jsrq inner join khdx_jg jg on jg.khdxdh = jgcy.jgkhdxdh INNER JOIN JXGZ_DKJXGZ jxgz ON hy.KHDXDH = jxgz.KHDXDH inner join table(FN_GETSJJG_JAVA_QXHY($jsny_ym$,$login_khdxdh$,0)) qxhy on hy.khdxdh=qxhy.khdxdh 
where hy.khdxdh in ($khdxdh$)  
and gzhz.tjrq between $qsny$01 and $jsny_ym$


/**!mycat:datanode=$db$**/ select count(1) from ( select substr(cast(gzhz.tjrq as char(8)),1,6) as tjrq, jg.jgdh, jg.jgmc, hy.hydh, hy.hymc, gzhz.dkyjjl, gzhz.dkzlkf, gzhz.bldkqsjl, gzhz.yfjxgz,jxgz.LXSRJX,jxgz.KHGLJX,jxgz.DKFFJX,jxgz.FYJDKKF,jxgz.FYJDKKFFH,jxgz.DKYJKZXM1, jxgz.DKYJKZXM2,jxgz.DKYJKZXM3,jxgz.DKKFKZXM1,jxgz.DKKFKZXM2 from dkjx_dkjxgzhz gzhz inner join khdx_hy hy on hy.khdxdh = gzhz.khdxdh inner join khdx_jgcy jgcy on jgcy.khdxdh = gzhz.khdxdh and gzhz.tjrq between jgcy.qsrq and jgcy.jsrq inner join khdx_jg jg on jg.khdxdh = jgcy.jgkhdxdh INNER JOIN JXGZ_DKJXGZ jxgz ON hy.KHDXDH = jxgz.KHDXDH inner join table(FN_GETSJJG_JAVA_QXHY($jsny_ym$,$login_khdxdh$,0)) qxhy on hy.khdxdh=qxhy.khdxdh 
