and gzhz.tjrq between $qsny$01 and $jsny_ym$ ) t

pageSetting.javascript:
jQuery(document).ready(function () { 
  var date = new Date();
  var qsny_num = $("#qsny").val();
  date.setYear(qsny_num.substring(0,4));
  var month_num = qsny_num.substring(4,5)=='0'?qsny_num.substring(5,6):qsny_num.substring(4,6);
  date.setMonth(qsny_num.substring(4,6)-1);
  date.setMonth(date.getMonth()-1);
  tempMonth = date.getMonth() < 9 ? '0'+(date.getMonth()+1):(date.getMonth()+1);
  $("#qsny").val(date.getFullYear()+""+tempMonth);

});

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
 var qsny_num = parseInt($("#qsny").val());
 var jsny_num = parseInt($("#jsny").val());
 if(jsny_num < qsny_num){
   parent.custom_alert("结束年月必须大于起始年月");
   return false;
 }

 var qsny_temp = createTimeObj($("#qsny").val());
 $("#qsny_yc").val(qsny_temp.yc);
 var jsny_temp = createTimeObj($("#jsny").val());
 $("#jsny_ym").val(jsny_temp.ym);
 //console.log($("#qsny_yc").val() + " " + $("#jsny_ym").val());
 return true;
}

function getDateStr(date){
  var year  = date.getFullYear();
  var month = date.getMonth()+1;
  var day   = date.getDate();
  if(month<10){month=0+""+month;}
  if(day<10){day=0+""+day;}
  return year+""+month+""+day;
}


