      /**!mycat:datanode=$db$**/ SELECT $tjrq$ as tjrq, JG.JGDH as jgdh, JG.JGMC as jgmc, C.HZXSE as hzxse, c. HZKHS as hzkhs, bb.cpmc as cpmc, bb.xse as xse, bb.khs as khs, bb.cpdm as cpdm FROM KHFX_JGLCXSBB BB INNER JOIN KHDX_JG JG ON JG.KHDXDH = BB.JGKHDXDH INNER JOIN( SELECT b.jgkhdxdh, cast(SUM(XSE)as decimal(25,4)) AS HZXSE, cast(SUM(KHS)as decimal(25,4)) AS HZKHS FROM ( SELECT XSE,KHS,JGKHDXDH FROM KHFX_JGLCXSBB HZBB where HZBB.TJRQ = (select ym from CSB_SJW where tjrq=cast(cast($tjrq$ as char(6))||'01' as integer) ) 
      AND HZBB.JGKHDXDH IN ($jgkhdxdh$)  
      AND HZBB.KHLX ='$khlx$'  and F_IS_EXISTS_STR('$select_box_hdn$',HZBB.cpdm,',')>0
      )B
    GROUP BY B.JGKHDXDH
)c on c.jgkhdxdh = bb.jgkhdxdh
