/**!mycat:datanode=$db$**/ select count(1) from (
select tjrq,jgmc,jgdh,ftpsr,zbjsr,tqzqlxhk,lxzc,(ftpsr+zbjsr+tqzqlxhk-lxzc) hj from( select yj.tjrq,jg.jgdh,jg.jgmc, sum(case when gl.pxxh=1 then yj.zbz else 0 end) ftpsr, sum(case when gl.pxxh=2 then yj.zbz else 0 end) zbjsr, sum(case when gl.pxxh=3 then yj.zbz else 0 end) tqzqlxhk, sum(case when gl.pxxh=4 then yj.zbz else 0 end) lxzc from khdx_zbbbgl gl inner join yjzb_jg_all yj on gl.zbdh=yj.zbdh and gl.bz=yj.bz inner join khdx_jg jg on yj.khdxdh=jg.khdxdh and jg.jgdj='$jgdj$' inner join table (FN_GETSJJG_JAVA_QX($tjrq$,$login_khdxdh$,0) ) qxjg on qxjg.khdxdh=jg.khdxdh where 1=1 and yj.tjrq=$tjrq$ and yj.tjkj='$tjkj$' and yj.sdbs='$sdbs$' and jg.khdxdh in($jgkhdxdh$) and gl.bbdh=1001 group by yj.tjrq,jg.jgdh,jg.jgmc,jg.pxbz order by jg.jgdh,jg.pxbz ) z
select tjrq, jgmc, jgdh, lxsr, ftpzc, jzzb, yswslxxz, (lxsr-ftpzc-jzzb-yswslxxz) hj from( select yj.tjrq, jg.jgmc, jg.jgdh, sum(case when gl.pxxh=1 then yj.zbz else 0 end) lxsr, sum(case when gl.pxxh=2 then yj.zbz else 0 end) ftpzc, sum(case when gl.pxxh=3 then yj.zbz else 0 end) jzzb, sum(case when gl.pxxh=4 then yj.zbz else 0 end) yswslxxz from khdx_zbbbgl gl inner join yjzb_jg_all yj on gl.zbdh=yj.zbdh and gl.bz=yj.bz inner join khdx_jg jg on yj.khdxdh=jg.khdxdh and jg.jgdj='$jgdj$' inner join table (fn_getsjjg_java_qx($tjrq$, $login_khdxdh$, 0) ) qxjg on qxjg.khdxdh=jg.khdxdh where 1=1 and yj.tjrq=$tjrq$ and yj.tjkj='$tjkj$' and yj.sdbs='$sdbs$' and jg.khdxdh in($jgkhdxdh$) and gl.bbdh=1002 group by yj.tjrq, jg.jgdh, jg.jgmc, jg.pxbz order by jg.jgdh, jg.pxbz desc ) z
) t

