sqlMap.select:

/**!mycat:datanode=$db$**/ select sum(yyjg) yyjg , nf , cast(yf as char(3)) || '月份' , '$qjms$' qjms, jg.jgdh , jg.jgmc , hy.hydh , hy.hymc from ( select khdxdh , tjrq , sum(yyjg) yyjg , cast(substr(cast(yy.tjrq as char(8)),1,4) as integer) nf , cast(substr(cast(yy.tjrq as char(8)),5,2) as integer) yf from khfa_yyjg_hy yy inner join ( select distinct ym from csb_sjw s where cast(substr(cast(s.tjrq as char(8)),1,4) as integer) =$nf$ ) t on yy.tjrq = t.ym group by khdxdh , tjrq ) a inner join khdx_hy hy on hy.khdxdh = a.khdxdh inner join khdx_jgcy cy on cy.khdxdh = a.khdxdh and a.tjrq between cy.qsrq and cy.jsrq inner join khdx_jg jg on jg.khdxdh = cy.jgkhdxdh where 1=1 
and a.nf = $nf$  
and a.yf= $yf$ group by yyjg , nf , yf , jg.jgdh , jg.jgmc , hy.hydh , hy.hymc order by jg.jgdh , hy.hydh


/**!mycat:datanode=$db$**/ select count(1) from ( select sum(yyjg) yyjg , nf , cast(yf as char(3)) || '月份' , '$qjms$' qjms, jg.jgdh , jg.jgmc , hy.hydh , hy.hymc from ( select khdxdh , tjrq , sum(yyjg) yyjg , cast(substr(cast(yy.tjrq as char(8)),1,4) as integer) nf , cast(substr(cast(yy.tjrq as char(8)),5,2) as integer) yf from khfa_yyjg_hy yy inner join ( select distinct ym from csb_sjw s where cast(substr(cast(s.tjrq as char(8)),1,4) as integer) =$nf$ ) t on yy.tjrq = t.ym group by khdxdh , tjrq ) a inner join khdx_hy hy on hy.khdxdh = a.khdxdh inner join khdx_jgcy cy on cy.khdxdh = a.khdxdh and a.tjrq between cy.qsrq and cy.jsrq inner join khdx_jg jg on jg.khdxdh = cy.jgkhdxdh where 1=1 
and a.nf = $nf$  
and a.yf= $yf$ group by yyjg , nf , yf , jg.jgdh , jg.jgmc , hy.hydh , hy.hymc order by jg.jgdh , hy.hydh ) t

