jQuery(document).ready(function () {
  jQuery('#zhlx').bind('change',setResult);
  jQuery('#khfs').bind('change',setResult);
  init_zhlx().pipe(init_khfs).pipe(function(){
  setResult();
  });

});

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
 return true;
}

function setResult(){

  jQuery('#result').val( jQuery('#zhlx').val()+ jQuery('#khfs').val());

}
