pageSetting.javascript:
jQuery(document).ready(function () {
$('#query_conn_panel').hide();
 if($('#dxlx').val() == '1' ){
     PAS.di.execute('wrlmx_wrlmxcsz',checkZzhBack);//判断有没有子账号
  }
  if($('#dxlx').val() == '2' ){
     PAS.di.execute('wrlmx_wrlmxcsz2',checkZzhBack);//判断有没有子账号
  }
});

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
 return true;
}

function checkZzhBack(obj){

  var dxlx = $('#dxlx').val();
  if(obj.data.length>0){
    if(obj.data[0].CSZ=='0' && dxlx == '1'){
      $('#dxlx2').val('1');
    }
    if(obj.data[0].CSZ=='1' && dxlx == '1'){
      $('#dxlx2').val('3');
    }
    if(obj.data[0].CSZ=='0' && dxlx == '2'){
       $('#dxlx2').val('2');
    }
    if(obj.data[0].CSZ=='1' && dxlx == '2'){

      $('#dxlx2').val('4');
    }}
}
