sqlMap.select:

      /**!mycat:datanode=$db$**/ select mx.sfzd,tz.tzdh,tz.tzrq,tz.bt,tzf.tzfmc,case when mx.sfyd='1' then '已读' else '未读' end sfyd from xtb_xttz tz inner join xtb_xttzfxxb tzf on tz.tzfid=tzf.tzfid inner join xtb_xttzmx mx on tz.tzdh=mx.tzdh where 1=1 
      and mx.khdxdh=$login_khdxdh$  and tz.zt='1' and $sysdate$ >= tz.tzrq
order by mx.sfzd desc,mx.sfyd asc,tz.tzrq desc,tz.tzdh desc


      /**!mycat:datanode=$db$**/ select count(1) from ( select mx.sfzd,tz.tzdh,tz.tzrq,tz.bt,tzf.tzfmc,case when mx.sfyd='1' then '已读' else '未读' end sfyd from xtb_xttz tz inner join xtb_xttzfxxb tzf on tz.tzfid=tzf.tzfid inner join xtb_xttzmx mx on tz.tzdh=mx.tzdh where 1=1 
      and mx.khdxdh=$login_khdxdh$  and tz.zt='1' and $sysdate$ >= tz.tzrq
order by mx.sfzd desc,mx.sfyd asc,tz.tzrq desc,tz.tzdh desc ) t
