/**!mycat:datanode=$db$**/ select count(1) from (
select jgdh,jgmc,a.lbmc,round(rlclye/jgsthyrs,2) as lbrjrlye from ( select jg.jgdh,jg.jgmc,hylb.lbmc,sum(zh.zhye*gx.zlbl*0.01) rlclye,jg.khdxdh from jxdx_ckzh zh inner join dxgx_hyyjgx_ck gx on zh.jxdxdh=gx.jxdxdh and gx.jsrq=29991231 inner join khdx_hylb hylb on gx.khdxdh=hylb.khdxdh and $tjrq$ between hylb.qsrq and hylb.jsrq inner join khdx_hy hy on hy.khdxdh=hylb.khdxdh inner join khdx_jg jg on zh.jgdh=jg.jgdh where zh.gxhslx='1' and hy.hydh not like 'XN%' and zh.khrq between $khrq$ and $tjrq$ and jg.khdxdh =$jgkhdxdh$ and zh.zhbs='$zhbs$' group by jg.jgdh,jg.jgmc,hylb.lbmc,jg.khdxdh union all select jg.jgdh,jg.jgmc,hylb.lbmc,sum(zh.zhye) rlclye,jg.khdxdh from jxdx_ckzh zh inner join khdx_jg jg on zh.jgdh=jg.jgdh inner join khdx_hylb hylb on zh.khdxdh=hylb.khdxdh and $tjrq$ between hylb.qsrq and hylb.jsrq inner join khdx_hy hy on zh.khdxdh=hy.khdxdh where zh.gxhslx='0' and hy.hydh not like 'XN%' and zh.khrq between $khrq$ and $tjrq$ and jg.khdxdh =$jgkhdxdh$ and zh.zhbs='$zhbs$' group by jg.jgdh,jg.jgmc,hylb.lbmc,jg.khdxdh ) a inner join ( select jgkhdxdh as khdxdh,count(1) as jgsthyrs,hylb.lbmc from khdx_jgcy cy inner join khdx_hy hy on cy.khdxdh=hy.khdxdh and cy.jsrq=29991231 and hy.hydh not like 'XN%' inner join khdx_hylb hylb on hylb.khdxdh=cy.khdxdh and $tjrq$ between hylb.qsrq and hylb.jsrq group by jgkhdxdh,lbmc ) b on a.khdxdh=b.khdxdh and a.lbmc=b.lbmc order by a.jgdh asc ,lbrjrlye desc
select jgdh,jgmc,a.lbmc,round(rlclye/jgsthyrs,2) as lbrjrlye from ( select jg.jgdh,jg.jgmc,hylb.lbmc,sum(zh.zhye*gx.zlbl*0.01) rlclye,jg.khdxdh from jxdx_dkzh zh inner join dxgx_hyyjgx_dk gx on zh.jxdxdh=gx.jxdxdh and gx.jsrq=29991231 inner join khdx_hylb hylb on gx.khdxdh=hylb.khdxdh and $tjrq$ between hylb.qsrq and hylb.jsrq inner join khdx_hy hy on hy.khdxdh=hylb.khdxdh inner join khdx_jg jg on zh.jgdh=jg.jgdh where zh.gxhslx='1' and hy.hydh not like 'XN%' and zh.khrq between $khrq$ and $tjrq$ and jg.khdxdh =$jgkhdxdh$ and zh.zhbs='$zhbs$' group by jg.jgdh,jg.jgmc,hylb.lbmc,jg.khdxdh union all select jg.jgdh,jg.jgmc,hylb.lbmc,sum(zh.zhye) rlclye,jg.khdxdh from jxdx_dkzh zh inner join khdx_jg jg on zh.jgdh=jg.jgdh inner join khdx_hylb hylb on zh.khdxdh=hylb.khdxdh and $tjrq$ between hylb.qsrq and hylb.jsrq inner join khdx_hy hy on zh.khdxdh=hy.khdxdh where zh.gxhslx='0' and hy.hydh not like 'XN%' and zh.khrq between $khrq$ and $tjrq$ and jg.khdxdh =$jgkhdxdh$ and zh.zhbs='$zhbs$' group by jg.jgdh,jg.jgmc,hylb.lbmc,jg.khdxdh ) a inner join ( select jgkhdxdh as khdxdh,count(1) as jgsthyrs,hylb.lbmc from khdx_jgcy cy inner join khdx_hy hy on cy.khdxdh=hy.khdxdh and cy.jsrq=29991231 and hy.hydh not like 'XN%' inner join khdx_hylb hylb on hylb.khdxdh=cy.khdxdh and $tjrq$ between hylb.qsrq and hylb.jsrq group by jgkhdxdh,lbmc ) b on a.khdxdh=b.khdxdh and a.lbmc=b.lbmc order by a.jgdh asc ,lbrjrlye desc
) t

