function customOnLoadSuccess(data){
  $("#data_panel .datagrid-body table tr").each(function(){
    var tr = $(this);
    if(tr.find("td:eq(13)").text()=="未完成评分"){
      tr.find("td:eq(13)").find("div").css("color","red");
    }
  });
}

