jQuery(document).ready(function () {
   PAS.di.execute('cdkyjfpmx_getCKYJGXMS',getCsz);//数据访问组件
   $('#query_conn_panel').hide();//隐藏查询按钮 
   jQuery('#tool_bar'). hide();//去掉按钮区域



});

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
 return true;
}


function getCsz(obj){
  setTimeout(function(){
    if(obj.data.length>0){
      if(obj.data[0].CSZ=='0'){
       $('td[field="ZZH"] div').hide();
      }
    }
  },400);
}
