sqlMap.select:
/**!mycat:datanode=$db$**/ select count(1) from (
SELECT F_TIMETOCHAR(RZ.JLSJ,'yyyy/mm/dd hh24:mi:ss') JLSJ, czy.hymc as xgry, case when jl.ggbz='0' then '旧数据' else '修改后' end as jlbz , jxdx.djbh, jxdx.dlzh, jg.jgdh, jg.jgmc, hy.hydh, hy.hymc, jl.qsrq, jl.jsrq, '百分比:' || f_numtochar(jl.zlbl) as fpbl, jg1.jgmc as qyjg, jxdx.qyrq, CASE WHEN rz.czlx = '01' THEN '新增' WHEN rz.czlx = '02' THEN '修改' ELSE '删除' END AS czlx from xtb_hygxrz_dfgz rz inner join xtb_hygxjl_dfgz jl on rz.jldh=jl.jldh inner join khdx_hy hy on hy.khdxdh=jl.khdxdh inner join khdx_jgcy cy on cy.khdxdh=hy.khdxdh and $jsrq$ between cy.qsrq and cy.jsrq inner join khdx_jg jg on jg.khdxdh=cy.jgkhdxdh INNER JOIN JXDX_DFGZDJB JXDX ON JXDX.DJBH=RZ.DJBH inner join pas.khdx_hy czy on czy.khdxdh = rz.xgrdh inner join khdx_jg jg1 on jg1.jgdh=jxdx.jgdh where 1=1 and rz.xgrdh=$khdxdh$ and rz.jlsj between f_inttotime($qsrq$,1) and f_inttotime($jsrq$,2) AND JXDX.DLZH LIKE '%$dlzh$%' order by rz.jlsj desc,jxdx.dlzh, jl.jldh, jl.ggbz asc
SELECT F_TIMETOCHAR(RZ.JLSJ,'yyyy/mm/dd hh24:mi:ss') JLSJ, czy.hymc as xgry, CASE WHEN jl.ggbz = '0' THEN '旧数据' ELSE '修改后' END AS jlbz, jxdx.djbh, jxdx.dlzh, jg.jgdh, jg.jgmc, hy.hydh, hy.hymc, jl.qsrq, jl.jsrq, '百分比:' || f_numtochar (jl.zlbl) AS fpbl, jg1.jgmc AS qyjg, jxdx.qyrq, CASE WHEN rz.czlx = '01' THEN '新增' WHEN rz.czlx = '02' THEN '修改' ELSE '删除' END AS czlx FROM xtb_hygxrz_dfgz rz INNER JOIN xtb_hygxjl_dfgz jl ON rz.jldh = jl.jldh INNER JOIN khdx_hy hy ON hy.khdxdh = jl.khdxdh INNER JOIN khdx_jgcy cy ON cy.khdxdh = hy.khdxdh AND $jsrq$ BETWEEN cy.qsrq AND cy.jsrq INNER JOIN khdx_jg jg ON jg.khdxdh = cy.jgkhdxdh INNER JOIN jxdx_dfgzdjb jxdx ON jxdx.djbh = rz.djbh INNER JOIN khdx_jg jg1 ON JG1.JGDH = JXDX.JGDH INNER JOIN PAS.KHDX_HY CZY on czy.khdxdh = rz.xgrdh WHERE 1 = 1 AND jl.khdxdh = $khdxdh$ AND rz.jlsj between f_inttotime($qsrq$,1) and f_inttotime($jsrq$,2) AND jxdx.dlzh LIKE '%$dlzh$%' ORDER BY rz.jlsj DESC, jxdx.dlzh, JL.JLDH, jl.ggbz ASC
) t

pageSetting.javascript:
jQuery(document).ready(function () {
   $('#cxfs').change(function(){
    var cxfs = $(this).val();
    var label = $('#khdxdh_mc').parent().prev();
    if(cxfs=='1'){
      label.text('操作员');
    }else if(cxfs=='2'){
      label.text('行员');
    }
  }).trigger('change');
  var o_qsrq = createTimeObj($("#qsrq").val());
  $("#qsrq").val(o_qsrq.yc);
});

