jQuery(document).ready(function () {
    var obj=createTimeObj(jQuery("#sysdate").val());
    if(jQuery("#sysdate").val()!=obj.ym){
        var sym=obj.sym;
        jQuery("#tjrq").val(sym.substr(0,6));
    }
});

