sqlMap.select:
/**!mycat:datanode=$db$**/ select count(1) from (
SELECT F_TIMETOCHAR(RZ.JLSJ,'yyyy/mm/dd hh24:mi:ss') AS JLSJ ,czy.hymc as xgry,qy.khh,czlxms.dmms as czlx,qy.khmc,jg.jgdh,jg.jgmc,hy.hydh,hy.hymc,jl.qsrq,jl.jsrq, '分配比例:' || F_NumToChar(jl.zlbl) as fpbl,jg1.jgmc as qyjg,qy.qyrq, case when jl.ggbz='0' then '旧数据' else '修改后' end as ggbz from xtb_hygxrz_dxt rz inner join xtb_hygxjl_dxt jl on rz.jldh = jl.jldh inner join jxdx_dxtqy qy on qy.qykey = rz.qykey inner join khdx_hy hy on hy.khdxdh = jl.khdxdh inner join khdx_jgcy cy on cy.khdxdh = hy.khdxdh and $jsrq$ between cy.qsrq and cy.jsrq inner join khdx_jg jg on jg.khdxdh = cy.jgkhdxdh and jg.zxrq>=$jsrq$ inner join khdx_jg jg1 on jg1.jgdh = qy.jgdh INNER JOIN CSB_DMMS CZLXMS ON LOWER(CZLXMS.DMMC)='rzczlx' AND RZ.CZLX=CZLXMS.DMZ inner join pas.khdx_hy czy on czy.khdxdh = rz.xgrdh where 1=1 and jl.khdxdh = $khdxdh$ and rz.jlsj between F_INTTOTIME($qsrq$,1) and F_INTTOTIME($jsrq$,2) AND QY.KHH LIKE '%$khh$%' order by rz.jlsj desc,qy.khh, jl.jldh
SELECT F_TIMETOCHAR(RZ.JLSJ,'yyyy/mm/dd hh24:mi:ss') AS JLSJ ,czy.hymc as xgry,qy.khh,czlxms.dmms as czlx,qy.khmc,jg.jgdh,jg.jgmc,hy.hydh,hy.hymc,jl.qsrq,jl.jsrq, '分配比例:' || F_NumToChar(jl.zlbl) as fpbl,jg1.jgmc as qyjg,qy.qyrq, case when jl.ggbz='0' then '旧数据' else '修改后' end as ggbz from xtb_hygxrz_dxt rz inner join xtb_hygxjl_dxt jl on rz.jldh = jl.jldh inner join jxdx_dxtqy qy on qy.qykey = rz.qykey inner join khdx_hy hy on hy.khdxdh = jl.khdxdh inner join khdx_jgcy cy on cy.khdxdh = hy.khdxdh and $jsrq$ between cy.qsrq and cy.jsrq inner join khdx_jg jg on jg.khdxdh = cy.jgkhdxdh and jg.zxrq>=$jsrq$ inner join khdx_jg jg1 on jg1.jgdh = qy.jgdh INNER JOIN CSB_DMMS CZLXMS ON LOWER(CZLXMS.DMMC)='rzczlx' AND RZ.CZLX=CZLXMS.DMZ inner join pas.khdx_hy czy on czy.khdxdh = rz.xgrdh where 1=1 and rz.xgrdh = $khdxdh$ and rz.jlsj between F_INTTOTIME($qsrq$,1) and F_INTTOTIME($jsrq$,2) AND QY.KHH LIKE '%$khh$%' order by rz.jlsj desc,qy.khh, jl.jldh
) t

pageSetting.javascript:
jQuery(document).ready(function () {
  $('#cxfs').change(function(){
    var cxfs = $(this).val();
    var label = $('#khdxdh_mc').parent().prev();
    if(cxfs=='1'){
      label.text('操作员');
    }else if(cxfs=='2'){
      label.text('行员');
    }
  }).trigger('change');

  //起始日期设计为服务器时间的月初  
  var jsrq= document.getElementById('jsrq').value; 
var qsrq =jsrq.toString().substring(0,6)+'01';
document.getElementById('qsrq').value=qsrq;

});

