pageSetting.javascript:
jQuery(document).ready(function () {
  $('#tool_bar').children().val("重跑失败").addClass("cbutton_bg_110");

  $('#wjzt').change(function(){
    var t = window.setInterval(function(){
      var wjzt = $('#wjzt').val();
      if(wjzt != undefined && wjzt.length > 0){
        window.clearInterval(t);
        if(wjzt == '-1'){
          $('#zt').val(' 1=1 ');
        }else {
          $('#zt').val('rz.wjzt=CAST('+ wjzt + ' AS char(1)) ');
        }
      }
    });
  }).trigger('change');

});

