function openLXSR(obj){  
    var pars = "{khdxdh:'"+obj['KHDXDH']+"',tjrq:'"+obj['TJRQ']+"'}";
    //alert(pars);
    querySubQuery(pars,'dklxsrmxcx','45','30','tag');

}
