pageSetting.javascript:
jQuery(document).ready(function () {
//这里的js脚本，会在页面加载完成后执行
    BindParent('bz','zbdh','change');
    BindParent('sdbs','zbdh','change');
    init_yjlx().pipe(function(){
      jQuery('#yjlx').bind('change',yjlxChange).trigger('change');
  });

    $("#query_table tr:eq(1)").append($("<td></td>").append($(".operation[value='查 询']")));
});
//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
  var tjnf=$('#qsrq').val().substring(0,4);
  var dbnf=$('#jsrq').val().substring(0,4);
  $('#tjnf').val(tjnf);
  $('#dbnf').val(dbnf);
  if($('#yjlx').val()=='fxzb'){
    if(tjnf==dbnf){
      $('#jzsjy').val('fxzb_nfxt');
    }else{
      $('#jzsjy').val('fxzb_nfbt');
    }
  }else{
    if(tjnf==dbnf){
      $('#jzsjy').val('khzb_nfxt');
    }else{
      $('#jzsjy').val('khzb_nfbt');
    }  
  }
  return true;
}

var witingBak = witing;
var witing=function(){witingBak(true);};

function yjlxChange(){
var yjlx = jQuery('#yjlx');
var khzb = jQuery('#khzb');
  if(yjlx.val()=='fxzb'){
    khzb.hide();
    khzb.parent().prev().hide();
    khzb.parent().hide();
    $("#main table tr").eq(3).show();
    $("#ywlb").show();
    $("#zbdh").show();
    $("#sdbs").show();
    $("#zbbz").show();
    $("#tjkj").show();
    $("#bz").show();
    $("#ywlb").parent().prev().show();
    $("#zbdh").parent().prev().show();
    $("#sdbs").parent().prev().show();
    $("#zbbz").parent().prev().show();
    $("#zbdh").parent().show();
    $("#tjkj").parent().prev().show();
    $("#bz").parent().prev().show();


  }else{
     khzb.show();
     khzb.parent().prev().show();
     khzb.parent().show();

     $("#ywlb").hide();
     $("#zbdh").hide();
     $("#sdbs").hide();
     $("#zbbz").hide();
     $("#tjkj").hide();
     $("#bz").hide();
     $("#ywlb").parent().prev().hide();
     $("#zbdh").parent().prev().hide();
     $("#zbdh").parent().hide();
     $("#sdbs").parent().prev().hide();
     $("#zbbz").parent().prev().hide();
     $("#tjkj").parent().prev().hide();
     $("#bz").parent().prev().hide();
     $("#main table tr").eq(3).hide();

  }
}
