function showDeleteDialog(sel){
  var csmc=sel["CSMC"];    
  if(csmc=='JXBG_START_TIMER_DAY' ||csmc == 'JXBG_START_TIMER_HOUR' || csmc == 'SERVICE_PORT'){            
    custom_alert("绩效报告关键参数，不允许删除!");
    return ;
  }
  custom_confirm('你确定要删除所选记录吗？','提示',function(){
    var colIds="CSMC,CSZ,CSMS";
    var colId=colIds.split(",");//所有的主键名
    var params="&"; //URL的参数
    for(var i=0;i<colId.length;i++){
      params+=colId[i].toLowerCase()+"="+selDataFilter(sel[colId[i]])+"&";
    }
    params=encodeURI(params.substring(0,params.length-1));
    //alert(params);
    $.post('/module/parser/manage/deleteData/jxbgcsgl',params,function(cb){
      custom_alert(cb);
      loadData();
    }).error(function(){
      custom_alert("发生内部错误，无法删除所选记录！");
    });
  });
}

