//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
 if(checkDate()){
   return true;
 }else{
   return false;
 }
}

function checkDate(){
  var pass = true;
  var qsrq = $('#qsrq').val();
  var jsrq = $('#jsrq').val();
  if(qsrq>jsrq){
   alert('起始日期不能大于结束日期');
   pass = false;
  }
  return pass;
}
