javascript.query:
jQuery(document).ready(function () {
 PAS.di.execute('nqrykqjlgl_getMaxtzrq',getMaxtzrqBack) ;    
});

function showDeleteDialog(sel){ 
     var tjrq=sel["TJRQ"];
     if(maxtzrq>tjrq){
        custom_alert("只能删除统计月份对应的月末在重算日期及以后"+maxtzrq+"的数据");
        return false;
     }
      custom_confirm('你确定要删除所选记录吗？','提示',function(){
            var colIds="TJRQ,KHDXDH,KQLX,KQTS,BZSM";
            var colId=colIds.split(",");//所有的主键名
            var params="&"; //URL的参数
            for(var i=0;i<colId.length;i++){
                params+=colId[i].toLowerCase()+"="+selDataFilter(sel[colId[i]])+"&";
            }
            params=encodeURI(params.substring(0,params.length-1));
           // alert(params);
           $.post('/module/parser/manage/deleteData/nqrykqjlgl',params,function(cb){
                custom_alert(cb);
                    loadData();
            }).error(function(){
                custom_alert("发生内部错误，无法删除所选记录！");
            });
        });   
}
function showUpdateEdtiDialog(sel){
    var tjrq=sel["TJRQ"];
     if(maxtzrq>tjrq){
        custom_alert("只能修改统计月份对应的月末在重算日期及以后"+maxtzrq+"的数据");
        return false;
     }
     var colIds="TJRQ,KHDXDH,JGDH,JGMC,HYDH,HYMC,KQLX,KQTS,BZSM,HYMC1";
        var colId=colIds.split(",");//所有的主键名
        var params="&"; //URL的参数
        for(var i=0;i<colId.length;i++){
            var data = selDataFilter(sel[colId[i]]);
            //alert("|"+data+"|");
            if(data==undefined){data=''}
            params+=colId[i].toLowerCase()+"="+data+"&";
        }
        params=encodeURI(params.substring(0,params.length-1));
        //alert(params);
        var width = editpage_width||700;    
        var height = editpage_height||400;    
        custom_window('/module/parser/manage/getById/nqrykqjlgl?operate=update'+params,width,height,'内勤人员考勤记录管理编辑');
    }

function getMaxtzrqBack(obj){
  if(obj.data.length>0){
        maxtzrq = obj.data[0].MAXTZRQ;
        PAS.di.setDone(obj.sqlid);
  }
}

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){   
     var obj=createTimeObj(jQuery("#tjyf").val());
     jQuery("#tjyf_ym").val(obj.ym);
 return true;
}

javascript.edit:
jQuery(document).ready(function () {
//这里的js脚本，会在页面加载完成后执行
PAS.di.execute('nqrykqjlgl_getMaxtzrq',getMaxtzrqBack);//加载最大的起始日期
  var obj=createTimeObj(jQuery("#tjrq").val());
  $("#tjyf_ym").val(obj.ym);
  if($("#page_operate").val()=="update"){
      jQuery($("#tjrq").attr("disabled","disabled"));
   }
});
//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
  var obj=createTimeObj($("#tjrq").val());
  $('#tjyf_ym').val(obj.ym);
  if(maxtzrq>parseInt($("#tjyf_ym").val(obj.ym))){
      parent.custom_alert("由于重算天数的限制，统计月份所在月末不能小于最小重跑日期");
      return false;
   }
 return true;
}


function getMaxtzrqBack(obj){
  if(obj.data.length>0){
        maxtzrq = obj.data[0].MAXTZRQ;    
        PAS.di.setDone(obj.sqlid);
  }
}
