jQuery(document).ready(function () {
    editpage_height=450;
    editpage_width=500;
  jQuery('#tjny').blur(function(){
    getYm();
  }).trigger('blur');

    PAS.di.execute('xnhyfpjxgzgl_cxjzbz',checkCxJzbz);//结转标志
    PAS.di.execute('xnhyfpjxgzgl_getcsrq',getCsrq);//参数日期
    $("#tjny").executeOnEvent('blur','xnhyfpjxgzgl_cxjzbz',checkCxJzbz);
});

