function customValidate(){
 return true;
}
function getYm(){
 var o_qsrq = createTimeObj(jQuery('#tjny').val()); 
 jQuery('#tjny_ym').val(o_qsrq.ym);
}
function showAddEditDialog(){
        var width = editpage_width||800;    
        var height = editpage_height||550;    
        custom_window('/module/parser/manage/getById/xnhyfpjxgzgl?operate=add',width,height,'虚拟行员绩效工资分配编辑');
}

function checkCxJzbz(obj){
  var tjrq = jQuery('#tjny').val(); 

  if(obj.data.length>0){
      if(obj.data[0].CSZ=='0'){
          //var b= tjrq+'01';
           var b=jQuery('#tjny_ym').val();
         $('#jzbz').val('sjw.tjrq ='+b);
      }
      if(obj.data[0].CSZ=='1'){
         //var dqrq= tjrq+'01';
          var dqrq=jQuery('#tjny_ym').val();
         var dnnc = tjrq.substring(0,4)+'0101'
         $('#jzbz').val('sjw.tjrq in(select   ym from csb_sjw where tjrq=ym and tjrq between '+dnnc+'  and '+dqrq+')');
      }
      if(obj.data[0].CSZ=='2'){
         //var dqrq= tjrq+'01';
         var dqrq=jQuery('#tjny_ym').val();
         var csrq= $('#csrq').val();
         $('#jzbz').val('sjw.tjrq in(select  ym from csb_sjw where tjrq=ym and tjrq between '+csrq+' and '+dqrq+')');

      }
  }
}
function getCsrq(obj){
jQuery('#csrq').val(obj.data[0].CSRQ);
}

javascript.edit:
jQuery(document).ready(function () {
//这里的js脚本，会在页面加载完成后执行
    $("#myForm").css('margin-left','-75px');
   //PAS.di.execute('xnhyfpjxgzgl_xnhyjzqsrq',checkXnhyjzqsrq);
   PAS.di.execute('xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);
  $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx); 
  $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);
  if($("#page_operate").val()=='add'){
       jQuery($("#tjrq").removeAttr("disabled"));
        var o_qsrq = createTimeObj(jQuery('#tjrq').val()); 
       jQuery('#tjrq_ym').val(o_qsrq.ym);
        $("#tjrq").blur(function(){
       var o_qsrq = createTimeObj(jQuery('#tjrq').val()); 
       jQuery('#tjrq_ym').val(o_qsrq.ym);
    }); 
  }else{
     jQuery('#hyjx').val(jQuery('#fpjx').val());
     PAS.di.execute('xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx); 
     PAS.di.execute('xnhyfpjxgzgl_selectKfpjx',selectKfpjx);
     jQuery($("#tjrq").attr("disabled","disabled"));
      $("#tjrq").val( jQuery('#tjrq_ym').val().substring(0,6));
  }
});
//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
   var kfpjx = $("#kfpjx").val();
   var fpjx = $("#fpjx").val();
   if(parseFloat(fpjx)>parseFloat(kfpjx)){
    custom_alert('分配绩效必须小于等于可分配绩效!');
      return false;
   }
   if($("#tjrq_ym").val() < $("#csdate").val())
   {
      custom_alert('统计日期不能小于：'+$("#csdate").val().substr(0,6));
      return false;
   }

   if(fucCheckLength($('#bzsm').val())>200){
        custom_alert('备注超长！');
        return false;
    }
  return true;
}

function selectXnhyjx(obj){

   if(obj.data.length>0){
      $("#xnhyjx").val(obj.data[0].ZJX); 
   }else{
      $("#xnhyjx").val("0"); 
   }
   if($("#page_operate").val()=='add'){
   PAS.di.execute('xnhyfpjxgzgl_selectKfpjx',selectKfpjx);
   $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_selectKfpjx',selectKfpjx);
    $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_selectKfpjx',selectKfpjx);
    }
}

function selectKfpjx(obj){
   if(obj.data.length>0){
     var kfpjx = obj.data[0].FPJX;
     var zjx = $("#xnhyjx").val();
     var hyjx= $("#hyjx").val();
     var jx =parseFloat(zjx)-parseFloat(kfpjx)+parseFloat(hyjx);
      $("#kfpjx").val(jx.toFixed(2)); 
   }else{
     $("#kfpjx").val($("#xnhyjx").val()); 
     PAS.di.setDone(obj.sqlid);
   }
}

function fucCheckLength(strTemp) {  
 var i,sum;  
 sum=0;  
 for(i=0;i<strTemp.length;i++)  
 {  
  if ((strTemp.charCodeAt(i)>=0) && (strTemp.charCodeAt(i)<=255))  
   sum=sum+1;  
  else  
   sum=sum+2;  
 }  
 return sum;  
} 

function checkXnhyjzqsrq(obj){
  if(obj.data.length>0){
    $('#xnhyqsrq').val(obj.data[0].CSZ);
    PAS.di.execute('xnhyfpjxgzgl_cxjzbz2',checkCxJzbz);//结转标志
  }
  PAS.di.setDone(obj.sqlid);
}

function checkCxJzbz(obj){
  var tjrq = jQuery('#tjrq').val();
  if(obj.data.length>0){
      if(obj.data[0].CSZ=='0'){
          var b= tjrq+'01';
        $('#jzbz').val('yyjg.tjrq  in(select ym from csb_sjw where tjrq=CAST('+b+' AS INTEGER))');
        PAS.di.execute('xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx); 
      //  PAS.di.execute('xnhyfpjxgzgl_selectKfpjx',selectKfpjx);
        $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);
        $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_cxjzbz2',checkCxJzbz);
        $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);
        $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_cxjzbz2',checkCxJzbz);
       }
      if(obj.data[0].CSZ=='1'){
         var dqrq= tjrq+'01';
         var dnnc = tjrq.substring(0,4)+'0101'
        $('#jzbz').val('yyjg.tjrq in(select distinct ym from csb_sjw where tjrq between '+dnnc+'  and '+dqrq+')');
        PAS.di.execute('xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx); 
      //  PAS.di.execute('xnhyfpjxgzgl_selectKfpjx',selectKfpjx);
        $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);
        $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_cxjzbz2',checkCxJzbz);
        $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);
        $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_cxjzbz2',checkCxJzbz);
     }
      if(obj.data[0].CSZ=='2'){
         var dqrq= tjrq+'01';
         var xnhyqsrq = $('#xnhyqsrq').val();
        $('#jzbz').val('yyjg.tjrq in(select distinct ym from csb_sjw where tjrq <='+dqrq+' and tjrq >= '+xnhyqsrq+')');
        PAS.di.execute('xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx); 
        $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);
        $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_selectXnhyjx',selectXnhyjx);

        $("#tjrq").executeOnEvent('blur','xnhyfpjxgzgl_cxjzbz2',checkCxJzbz);
        $("#khdxdh").executeOnEvent('change','xnhyfpjxgzgl_cxjzbz2',checkCxJzbz);
      }
  }
  PAS.di.setDone(obj.sqlid);
}
