sqlMap.select:
/**!mycat:datanode=$db$**/ select count(1) from (
select f_timetochar(gxrz.jlsj,'yyyy/mm/dd hh24:mi:ss') jlsj, czy.hymc as czymc, case when gxjl.ggbz='0' then '旧数据' else '修改后' end as ggbz, czlxms.dmms as czlx, zh.zhdh as zh, zh.khh, zh.zhhm as khmc, jg.jgdh, jg.jgmc, hy.hydh, hy.hymc, gxjl.zlbl, gxjl.clbl, gxjl.clje yz, gxjl.qsrq, gxjl.jsrq from pas.xtb_zhgxrz_$dxlx$ gxrz inner join pas.xtb_zhgxjl_$dxlx$ gxjl on gxjl.jldh = gxrz.jldh inner join pas.jxdx_$dxlx$zh zh on zh.jxdxdh = gxrz.jxdxdh and zh.zhdh like '%$zhdh$%' inner join pas.khdx_jgcy jgcy on jgcy.khdxdh = gxjl.khdxdh and $jsrq$ between jgcy.qsrq and jgcy.jsrq inner join pas.khdx_jg jg on jg.khdxdh = jgcy.jgkhdxdh and jg.zxrq >= $jsrq$ inner join pas.khdx_hy hy on hy.khdxdh = gxjl.khdxdh inner join pas.khdx_hy czy on czy.khdxdh = gxrz.xgrdh inner join pas.csb_dmms czlxms on czlxms.dmmc='RZCZLX' and gxrz.czlx=czlxms.dmz where 1=1 and gxrz.xgrdh = $khdxdh$ and gxrz.jlsj between f_inttotime($qsrq$,1) and f_inttotime($jsrq$,2) order by gxrz.jlsj desc, zh.zhdh asc, gxjl.jldh asc, gxjl.ggbz asc
select f_timetochar(gxrz.jlsj,'yyyy/mm/dd hh24:mi:ss') jlsj, czy.hymc as czymc, case when gxjl.ggbz='0' then '旧数据' else '修改后' end as ggbz, czlxms.dmms as czlx, zh.zhdh as zh, zh.khh, zh.zhhm as khmc, jg.jgdh, jg.jgmc, hy.hydh, hy.hymc, gxjl.zlbl, gxjl.clbl, gxjl.clje yz, gxjl.qsrq, gxjl.jsrq from pas.xtb_zhgxrz_$dxlx$ gxrz inner join pas.xtb_zhgxjl_$dxlx$ gxjl on gxjl.jldh = gxrz.jldh inner join pas.jxdx_$dxlx$zh zh on zh.jxdxdh = gxrz.jxdxdh and zh.zhdh like '%$zhdh$%' inner join pas.khdx_jgcy jgcy on jgcy.khdxdh = gxjl.khdxdh and $jsrq$ between jgcy.qsrq and jgcy.jsrq inner join pas.khdx_jg jg on jg.khdxdh = jgcy.jgkhdxdh and jg.zxrq >= $jsrq$ inner join pas.khdx_hy hy on hy.khdxdh = gxjl.khdxdh inner join pas.khdx_hy czy on czy.khdxdh = gxrz.xgrdh inner join pas.csb_dmms czlxms on czlxms.dmmc='RZCZLX' and gxrz.czlx=czlxms.dmz where 1=1 and gxjl.khdxdh = $khdxdh$ and gxrz.jlsj between f_inttotime($qsrq$,1) and f_inttotime($jsrq$,2) order by gxrz.jlsj desc, zh.zhdh asc, gxjl.jldh asc, gxjl.ggbz asc
) t

pageSetting.javascript:
jQuery(document).ready(function ($) {
//这里的js脚本，会在页面加载完成后执行
  $('#cxfs').change(function(){
    var cxfs = $(this).val();
    var label = $('#khdxdh_mc').parent().prev();
    if(cxfs=='1'){//操作员
      label.text('操作员');
    }else if(cxfs=='2'){//行员
      label.text('行员');
    }
  }).trigger('change');


  var o_qsrq = createTimeObj($("#qsrq").val());
  $("#qsrq").val(o_qsrq.yc);

});

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
  var qsrq = jQuery('#qsrq').val();
 var jsrq = jQuery('#jsrq').val();
  if (qsrq>jsrq) {
  $('#qsrq').val(jsrq);
  $('#jsrq').val(qsrq);
  }
 return true;
}
