pageSetting.javascript:
jQuery(document).ready(function () {
  $('#cxfs').change(function(){
    var cxfs = $(this).val();
    var label = $('#khdxdh_mc').parent().prev();
    if(cxfs=='1'){//操作员
      label.text('操作员');
    }else if(cxfs=='2'){//行员
      label.text('行员');
    }
  }).trigger('change');

});

//自定义验证，在提交时执行。通过返回true，否则返回false
function customValidate(){
   var ywlx = document.getElementById('ywlx').value;
    var cxfs = document.getElementById('cxfs').value;
    document.getElementById('jgj').value=ywlx+'_'+cxfs
    //alert(document.getElementById('jgj').value);
 if(checkDate()){
   return true;
 }else{
   return false;
 }

}


function checkDate(){
  var pass = true;
  var qsrq = $('#qsrq').val();
  var jsrq = $('#jsrq').val();
  if(qsrq>jsrq){
   alert('起始日期不能大于结束日期');
   pass = false;
  }
  return pass;
}
